import React, { Component } from 'react'
import { Menu } from 'semantic-ui-react'

import AboutMe from './aboutMeComponent'
import Music from './musicComponent'
import Blog from './blogComponent'
import Academia from './academiaComponent.js'

export default class MainPageWithMenu extends React.Component {
  state = { activeItem: "blog" }
  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  getComponent = (item) => {
    // if (item === 'about me') {
    //   return <AboutMe />
    // }
    if (item === 'music') {
      return  <Music/>
    }
    if (item === 'blog') {
      return <Blog />
    }
    if (item === 'academia') {
      return <Academia />
    }
  }

  render() {
    const { activeItem } = this.state
    const componentToShow = this.getComponent(activeItem)
    return (
      <Router>
      <div className="mainPageWithMenu">
        <Menu className='menu' fluid borderless widths={3}>
          {/* <Menu.Item name='about me' active={activeItem === 'about me'} onClick={this.handleItemClick} /> */}
          <Menu.Item name='blog' active={activeItem === 'blog'} onClick={this.handleItemClick} />
          <Menu.Item name='music' active={activeItem === 'music'} onClick={this.handleItemClick} />
          <Menu.Item name='academia' active={activeItem === 'academia'} onClick={this.handleItemClick} />
        </Menu>

        {componentToShow}

      </div>
      <Switch>
        <Route exact path='/blog' component={Blog} />
        <Route path='/music' component={Music} />
        <Route path='/academia' component={Academia} />
      </Switch>
      </Router>
    )
  }
}


<Router>
<div>
  <h2>Welcome to React Router Tutorial</h2>
  <nav className="navbar navbar-expand-lg navbar-light bg-light">
  <ul className="navbar-nav mr-auto">
    <li><Link to={'/blog'} className="nav-link">Blog</Link></li>
    <li><Link to={'/music'} className="nav-link">Contact</Link></li>
    <li><Link to={'/academia'} className="nav-link">About</Link></li>
  </ul>
  </nav>
  <hr />
  <Switch>
      <Route exact path='/blog' component={Blog} />
      <Route path='/music' component={Music} />
      <Route path='/academia' component={Academia} />
  </Switch>
</div>
</Router>
