import React, { Component } from 'react'
import { Container, Header, Divider } from 'semantic-ui-react'


export default class Blog extends Component {

  constructor(props){
    super(props);
    this.state  = {
        posts: [],
    };
  };

  componentDidMount() {
    httpService.getPosts('txt_posts').then(function (result) {
        this.setState({ posts: result.data})
    }.bind(this));
  }

  render() {
    const posts = require('../data/posts.json');
    const t = posts.map(element =>
      <li key={element.title}>
        <br />
        <Container text className="farsiPost">
          <Header as='h3' content={element.title} className={element.className} />
          {element.text.map(x => <p className={element.className + ' post'}>{x}</p>)}
        </Container>
        <Header as='h3' subheader={'- ' + element.date} className={element.className} />
        <Divider />
      </li>
    );
    return (
      <Container text className="post">
        <ul className="blogList">
          {t}
        </ul>
      </Container>
    )
  }
}
