import React from 'react'
import { Divider, Container, Header } from 'semantic-ui-react'

import  PostService  from  './postService';
const  postService  =  new  PostService();


export default class Music extends Comment {

constructor(props){
  super(props);
  this.state  = {
      posts: [],
      nextPageURL:  ''
  };

  this.nextPage  =  this.nextPage.bind(this);
  this.handleDelete  =  this.handleDelete.bind(this);
}

componentDidMount() {
  var  self  =  this;
  postService.getPostss().then(function (result) {
      console.log(result);
      self.setState({ posts: result.data, nextPageURL:  result.nextlink})
  });
}
handleDelete(e, pk){
  var  self  =  this;
  postService.deletePosts({pk :  pk}).then(()=>{
      var  newArr  =  self.state.posts.filter(function(obj) {
          return  obj.pk  !==  pk;
      });

      self.setState({posts:  newArr})
  });
}

nextPage(){
  var self = this;
  console.log(this.state.nextPageURL);        
  postService.getPostssByURL(this.state.nextPageURL).then((result) => {
      self.setState({ posts:  result.data, nextPageURL:  result.nextlink})
  });
}

render(){

  const soundclouds = self.state.posts;

  var t = soundclouds.map(element =>
    <div>
      <Container text>
        <Header as='h3'>{element.title}</Header>
        <p>{element.text}</p>
        {element.farsitext ? <p className="farsiPost">{element.farsitext}</p> : null}
        <iframe title={element.title} width="100%" height={element.playlist === true ? 350 : 150} scrolling="no" frameborder="no" allow="autoplay" src={element.src}></iframe>
      </Container>
      <Divider />
    </div>
  );

return(
  <Container text>
    <br />
    <p className='nicep post'>Music, and playing music is my [main] hobby. I play different music instruments and always like to try new ones. Piano and Setar (an Iranian music Instrument) are the ones I play for a longer time, but lately I've been practicing Trumpet too.</p>
    <p>I record time to time the stuff I play or the sounds I hear. Based on the recordings, I'm gathering two collections which are:</p>
    <ul>
      <li><p>Improvisation for Home: a collection of my improvisations.</p></li>
      <li><p>Through the Cities: a collection of street music of different cities that I have visited.</p></li>
    </ul>
    <p>They probably lack many quality standards but I still like to continue recording, at least for a while.</p>
    <Divider />
    {t}
  </Container>)
}
}
