import React, { Component } from 'react'
import { Container, Header, Divider } from 'semantic-ui-react'


export default class Blog extends Component {

  render() {
    const posts = require('../data/posts.json');
    const t = posts.map(element => 
      <li key={element.title}>
      <br/>
      <Container text className="farsiPost">
        <Header as='h3' className={element.className}>{element.title}</Header>
        <p className={element.className + ' post'}>
          {element.text}
        </p>
      </Container>
      <br/>
      <Divider/>
      </li>
    );
    return (
      <Container text className="post">
      <p className='nicep'>
      I sometimes write, mostly in Farsi (always). I usually post them on facebook, but I have also added some of them here.
    </p>
    <Divider/>
        <ul className = "blogList">
          {t}
        </ul>
        </Container>
    )
  }
}
