import React, { Component } from 'react'
import { Menu } from 'semantic-ui-react'

import AboutMe from './aboutMeComponent'
import Music from './musicComponent'
import Blog from './blogComponent'
import Academia from './academiaComponent.js'

export default class MainPageWithMenu extends Component {
  state = { activeItem: "about me" }
  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  getComponent = (item) => {
    if (item === 'about me') {
      return <AboutMe />
    }
    if (item === 'music') {
      return  <Music/>
    }
    if (item === 'blog') {
      return <Blog />
    }
    if (item === 'academia') {
      return <Academia />
    }
  }

  render() {
    const { activeItem } = this.state
    const componentToShow = this.getComponent(activeItem)
    return (
      <div className="mainPageWithMenu">
        <Menu className='menu' fluid borderless widths={4}>
          <Menu.Item name='about me' active={activeItem === 'about me'} onClick={this.handleItemClick} />
          <Menu.Item name='blog' active={activeItem === 'blog'} onClick={this.handleItemClick} />
          <Menu.Item name='music' active={activeItem === 'music'} onClick={this.handleItemClick} />
          <Menu.Item name='academia' active={activeItem === 'academia'} onClick={this.handleItemClick} />
        </Menu>

        {componentToShow}

      </div>

    )
  }
}
