import React, { Component } from 'react'
import { Container, Header, Divider, Button } from 'semantic-ui-react'

import  HTTPService  from  '../httpService';
const  httpService  =  new  HTTPService();

export default class Blog extends Component {

  constructor(props){
    super(props);
    this.state  = {
        posts: [],
        nextPageURL:  ''
    };
    this.nextPage  =  this.nextPage.bind(this);
  };

  componentDidMount() {
    httpService.getPosts('txt_posts').then(function (result) {
        this.setState({ posts: result.data,
                        nextPageURL:  result.nextlink,
                        prevPageURL:  result.prevlink})
    }.bind(this));

    alert(this.state.nextlink)
  }

  nextPage(){
    var  self  =  this;
    httpService.getPostsByURL(this.state.nextPageURL).then((result) => {
        self.setState({ posts:  result.data, prevPageURL: this.state.nextPageURL, nextPageURL:  result.nextlink})
    });
  }

  prevPage(){
      httpService.getPostsByURL(this.state.prevPageURL).then((result) => {
      this.setState({ posts:  result.data, nextPageURL: this.state.prevPageURL, prevPageURL:  result.prevlink})
    });
  }

  render() {
    const posts = this.state.posts;
    const t = posts.map(element =>
      <li key={element.title}>
        <br />
        <Container text className={element.className}>
          <Header as='h3' content={element.title} className={element.className} />
          {/* <h4 className='post'>{element.description}</h4>/> */}
          <p className={element.className}>{element.body}</p>
        </Container>
        <Header as='h3' subheader={'- ' + element.date} className={element.className} />
        <Divider />
      </li>
    );

    return (
      <Container text>
        <ul className="blogList">
          {t}
        </ul>
        <Button.Group className>
          <Button icon='chevron left' onClick=  {  this.prevPage  } />
          <Button icon='chevron right' onClick=  {  this.nextPage  }/>
        </Button.Group>
      </Container>
    )
  }
}
