import React, {Component} from 'react'
import { Dimmer, Card, Image, Icon } from 'semantic-ui-react'


import ReviewDetail from './reviewDetailComponent'

import  HTTPService  from  '../httpService';
const  httpService  =  new  HTTPService();


export default class Review extends Component {

constructor(props){
  super(props);
  this.state  = {
      active: false,
      pageUrl:  this.props.review.pageUrl
    };

  this.nextPage  =  this.nextPage.bind(this);
  this.prevPage  =  this.prevPage.bind(this);
};

componentDidMount() {
  httpService.getPosts('sc_posts').then(function (result) {
      this.setState({ posts: result.data,
                      nextPageURL:  result.nextlink,
                      prevPageURL:  result.prevlink})
  }.bind(this));
}

nextPage(){
  httpService.getPostsByURL(this.state.nextPageURL).then((result) => {
      this.setState({ posts:  result.data, prevPageURL: this.state.nextPageURL, nextPageURL:  result.nextlink})
  });
}

prevPage(){
  httpService.getPostsByURL(this.state.nextPageURL).then((result) => {
      this.setState({ posts:  result.data, prevPageURL: result.prevlink, nextPageURL:  result.nextlink})
  });
}

handleShow = () => (this.setState({active: true}))
handleHide = () => (this.setState({active: false}))


render(){

let active = this.state.active;
let content = this.props.review.bandName

return(
  <div>

    <ReviewDetail activeModal={this.state.activeModal}/>
  <Card onClick={this.changeActiveModal}>
  {/* <Image src={this.props.review.imageUrl} wrapped ui={false} /> */}
  <Image   as={Dimmer.Dimmable}
  blurring={true}
  dimmed={this.state.active}
  dimmer={{active, content}}
  onMouseEnter={this.handleShow}
  onMouseLeave={this.handleHide}
  src={this.props.review.imageUrl || "https://react.semantic-ui.com/images/avatar/large/steve.jpg"} wrapped ui={false}
  size='medium'/>
  <Card.Content className='farsiPost'>
    {this.props.review.title}
  </Card.Content>

  <Card.Content extra>
        <span className='farsiPost' className='date'>{this.props.review.date}</span>
        <Icon name='calendar alternate' />
  </Card.Content>
  </Card>
  </div>
  )
}
}
