import React, {Component} from 'react'
import { Image, Icon, Modal, Header, Button } from 'semantic-ui-react'

export default class ReviewDetail extends Component {

  render(){
    return(
      <Modal open={this.props.activeModal} closeIcon={true} onClose={this.props.handleClose} className='farsiModal'>
      <Modal.Header>Profile Picture</Modal.Header>
      <Modal.Content image scrolling>
        <Image size='large' src={this.props.review.imageUrl || "https://react.semantic-ui.com/images/avatar/large/steve.jpg"} wrapped />
        <Modal.Description>
          <Header>{this.props.review.title}</Header>
          {this.props.review.body.split('\n').map(x => <p className={this.props.review.className || 'farsiPost'}>{x}</p>)}
        </Modal.Description>
      </Modal.Content>
      <Modal.Actions>
      </Modal.Actions>
    </Modal>
    )
  }
}
